bool dfs(VI a){
    int n = a.size();
    int e = 0, o;
    REP(i,n) e += (a[i] % 2 == 0);
    if (e % 2 == 1) return true;
    o = n - e;
    if (o != 1) return false;
    REP(i,n){
        if (a[i] % 2 == 1){
            if (a[i] == 1) return false;
            a[i]--;
        }
    }
    int d = gcd(a[0],a[1]);
    REP(i,n) d = gcd(d, a[i]);
    REP(i,n) a[i] /= d;
    return dfs(a) ^ 1;
}

