		if(cnt[0] % 2 == 1 && cnt[1] >= 1) { puts("First"); return; }
		if(cnt[0] % 2 == 0 && cnt[1] >= 2) { puts("Second"); return; }
		if(cnt[0] % 2 == 0 && cnt[1] == 1)
		{
			for(int i = 1; i <= n; ++i) if(a[i] % 2 == 1) --a[i];
			int d = 0;
			for(int i = 1; i <= n; ++i) d = __gcd(d, a[i]);
			for(int i = 1; i <= n; ++i) a[i] /= d;
		}
	}
}

