bool dp(int n,vector<ll> v){
  ll sum=0 ,cnt=0;
  rep(i,v.size()){
    sum+=v[i];
    sum%=2;
    if(v[i]&1)cnt++;
    }
  if(sum){
    if(!(n&1)) return true;
    if(cnt>2)return false;
    bool ok=true;
    while(ok){
      rep(i,n){
        v[i]>>=1;
        if(v[i]&1)ok=false;
        }
      }    
    return !dp(n,v);
    }
  else{
    if(n%2)return true;
    return false;
