LL gcd(LL a, LL b){
	int ans=0;
	if(a<=1 or b<=1) return 1;
	while((a+1)&(b+1)&1==1){
		ans++;
		a>>=1;
		b>>=1;
	}
	return 1LL<<ans;
}
bool solve(){
