				else cout << "First\n";
			}
			return 0;
		}
		int cnt = 0;
		for(int i=0;i<n;++i) if(arr[i]%2 == 0) ++cnt;
		if(cnt&1){
			if(fl) cout << "Second\n";
			else cout << "First\n";
			return 0;
		}else if(n-cnt > 1){
			if(fl) cout << "First\n";
			else cout << "Second\n";
			return 0;
		}
		if(*max_element(arr , arr+n) == 1){
			if(!fl) cout << "Second\n";
			else cout << "First\n";
			return 0;
		}
		for(int i=0;i<n;++i) if(arr[i]&1) arr[i]--;
		int g = -1;
		for(int i=0;i<n;++i){
			if(g == -1) g = arr[i];
			else g = __gcd(g , arr[i]);
		}
		for(int i=0;i<n;++i) arr[i]/=g;
		fl = !fl;
	}
	return 0;
