int main()
{
    int N;
    std::cin >> N;
    std::vector<ll> A(N);
    std::cin >> A;
    auto ok = [&](auto&& self, std::vector<ll>& A) {
        int even = 0;
        for (int i = 0; i < N; i++) { even += (A[i] % 2 == 0 ? 1 : 0); }
        if (even % 2 == 1) {
            return true;
        } else if (even >= 2) {
            return false;
        } else {
            for (int i = 0; i < N; i++) { A[i] /= 2; }
            return not self(self, A);
        }
    };
    std::cout << (ok(ok, A) ? "First" : "Second") << std::endl;
    return 0;
}

