bool solve(vector<int64_t> &as) {
  int64_t rem = 0;
  int64_t odds = 0;

  int64_t mi = as[0];
  for (auto &a : as) {
    rem += a - 1;
    if (a % 2 == 1)
      odds++;

    mi = min(mi, a);
  }
  bool win = rem % 2 == 1;

  if (mi > 1 && odds == 1) {
    for (auto &a : as) {
      a /= 2;
    }
    win |= !solve(as);
  }

  return win;
}

