				cnt += dp[xmp - 1][v] * min(c, i);
			} else if ((xpm = pm[cp])) {
				for (int u: bad[xpm - 1])
					if (is_par(u, v)) {
//						cerr << m << '-' << i << '-' << u << endl;
						cnt += min(c, i);
					}
			}
			cp *= m;
		}
//		cerr << v << ' ' << val << ' ' << m << ' ' << cnt << endl;
		res = res * tav(m, cnt) % MOD;
	}
//	cerr << v << '*' << val << '*';
//	cerr << res << endl;
	return res;
}


int get_pr(int x, int val) {
	if (!x)
		return 1;
	return get(fad[x][0], val);
}

void gharbal() {
	for (int i = 2; i < MX; i++) 
		if (!par[i]) 
			for (int j = i; j < MX; j += i)
				if (!par[j])
					par[j] = i;
}

void dfs(int v = 0, int p = 0) {
	static int t = 0;
	fad[v][0] = p;
	for (int i = 1; i < L; i++)
		fad[v][i] = fad[fad[v][i - 1]][i - 1];
	h[v] = h[p] + 1;
	st[v] = t++;
	for (auto u: adj[v])
		if (u != p)
			dfs(u, v);
	fn[v] = t;
}

void dfs2(int v, int p, int w, int sv = 0) {
	sv += (a[v] % w == 0 && a[v] % (w * par[w]) != 0);
	//cerr << v << ' ' << w << ' ' << sv << endl;
	dp[col][v] = sv;
	for (auto u: adj[v]) {
		if (u == p) continue;
		dfs2(u, v, w, sv);
	}
}

void add(int w, int v) {
	auto vec = mg_all(w);
	for (int u: vec)
		res.push_back({u, v});
}

void build() {
	sort(res.begin(), res.end());
	while (res.size()) {
		int x = res.back().F, cnt = 0;
		vector <int> ash;
		while (res.size() && res.back().F == x) 
			cnt++, ash.push_back(res.back().S), res.pop_back();
		if (cnt > SQ) {
			dfs2(0, 0, x);
			mp[x] = ++col;
		} else {
			for (int u: ash) 
				bad[loc].push_back(u);
			pm[x] = ++loc;
		}
	}
}

