int n, a[maxn];

bool solve()
{
	int o = 0;
	fori (i, 0, n)
		if (a[i] & 1)
			o++;
	int e = n - o;
	if (e & 1)
		return true;
	else
	{
		if (o == 1)
		{
			int val = -1;
			fori (i, 0, n)
				if (a[i] & 1)
					val = a[i];
			if (val == 1)
				return false;
			val--;
			fori (i, 0, n)
				if (!(a[i] & 1))
					val = __gcd(val, a[i]);
			fori (i, 0, n)
				a[i] /= val;
			return !solve();
		}
		else if (o > 1)
			return false;
	}
	return false;
}

