int getans(int now)
{
	Rep(i,1,n)cnt+=(a[i]^1)&1;
	if(cnt&1)return now;
	else
	{
		if(cnt<n-1||a[1]==1)return now^1;
		static int x;x=0;
		Rep(i,1,n)
		{
			if(a[i]&1)--a[i];
			x=gcd(a[i],x);
		}
		Rep(i,1,n)a[i]/=x;
		return getans(now^1);
	}
}

