bool special(int a, int b){
    if(a == 1 && b == 1)
        return false;
    int a1 = a-1, b1 = b;
    int a2 = a, b2 = b-1;
    int g1 = gcd(a1,b1);
    int g2 = gcd(a2,b2);
    a1 /= g1;
    b1 /= g1;
    a2 /= g2;
    b2 /= g2;
    if((a1 && !special(a1,b1)) || (b2 && !special(a2,b2)))
        return true;
    return false;
}
