    for(int i = 1; i <= n; ++i)
        pleft[i] = gcd(pleft[i-1],arr[i]);
    for(int i = 1; i <= n; ++i)
        pright[i] = gcd(pright[i-1],arr[n-i+1]);
    for(int i = 1; i <= n; ++i){
        ll cur_gcd = gcd(arr[i]-1,__gcd(pleft[i-1],pright[n-i]));
        assert((sum-1)%cur_gcd == 0);
        ll cur_sum = (sum-1)/cur_gcd;
        if(cur_sum % 2 == 0)
            winner(0);
    }
    winner(1);
}

