vector<ll> vec;

bool solve() {
	vector<ll> pr, suf;
	pr.push_back(0);
	suf.push_back(0);
	for (int i = 0; i < vec.size(); i++) {
		pr.push_back(gcd(pr.back(), vec[i]));
	}
	for (int i = vec.size() - 1; i >= 0; i--) {
		suf.push_back(gcd(suf.back(), vec[i]));
	}
	reverse(suf.begin(), suf.end());

	for (int i = 0; i < vec.size(); i++) {
		if (vec[i] == 1) continue;
		ll g = gcd(pr[i], suf[i + 1]);
		g = gcd(g, vec[i] - 1);
		if (g != 1) {
			vec[i]--;
			for (int j = 0; j < vec.size(); j++) {
				vec[j] /= g;
			}
			bool k = solve();
			for (int j = 0; j < vec.size(); j++) {
				vec[j] *= g;
			}
			vec[i]++;
			if (!k) return true;
		}
	}

	return false;
}

