int rec(int n, vector<long long>& a, int t){
  int even = 0;
  int one  = 0;
  for(int i=0; i<n; i++){
    if(a[i]%2 == 0) even++;
    if(a[i] == 1) one++;
  }
  if(one != 0){
    return even%2==1 ? t : !t;
  }

  if(even%2 == 1) return t;
  else if(even == n-1){
    for(int i=0; i<n; i++){
      if(a[i] % 2 == 1) a[i]--;
      a[i] /= 2;
    }
    return rec(n, a, !t);
  }else{
    return t^1;
  }
}

