const int MAXN = 1e5 + 5;

int N;
int A[MAXN];

void input()
{
    read(N);
    for (int i = 1; i <= N; ++i) {
        read(A[i]);
    }
}

void solve()
{
    if (N == 2) {
        puts((A[1] + A[2]) & 1 ? "First" : "Second");
        return;
    }

    for (int side = 0; ; side ^= 1) {
        LL cntodd = 0, sum = 0, cnt1 = 0;
        for (int i = 1; i <= N; ++i) {
            sum += A[i];
            if (A[i] & 1)
                ++cntodd;
            if (A[i] == 1)
                ++cnt1;
        }
        if ((sum - N) & 1) {
            puts(side ? "Second" : "First");
            break;
        }
        if (cnt1 != 0 || cntodd != 1) {
            puts(side ? "First" : "Second");
            break;
        }
        int g = 0;
        for (int i = 1; i <= N; ++i) {
            if (A[i] & 1) --A[i];
            g = std::__gcd(g, A[i]);
        }
        assert(g != 1);
        for (int i = 1; i <= N; ++i) {
            A[i] /= g;
        }
    }
}

