int mygcd(int x,int y){
	while((x%=y)&&(y%=x));
	return x^y;
}

int a[100000];

int play(int n){
	int odd=0,even=0;
	for(int i=0;i<n;i++)
		if(a[i]&1)	odd++;
		else	even++;
	if(even&1)	return 0;
	else if(odd>=2)	return 1;
	else	return 2;
}

