const int N_ = 100500;

int N;
ll A[N_], pref[N_], suff[N_];

ll gcd (ll x, ll y) {
	return y ? gcd(y, x%y) : x;
}


int main() {
	scanf("%d", &N);
	for(int i = 1; i <= N; i++) {
		scanf("%lld", &A[i]);
	}

	if(N <= 2) {
		return 0 & puts(N == 2 ? "First" : "Second");
	}

	for(int step = 0; ; step += 1) {
		if(accumulate(A+1, A+N+1, -(ll)N) % 2 == 1) {
			return 0 & puts(step % 2 == 0 ? "First" : "Second");
		}

		for(int i = 1; i <= N; i++) {
			pref[i] = gcd(pref[i-1], A[i]);
		}
		for(int i = N; i > 0; i--) {
			suff[i] = gcd(suff[i+1], A[i]);
		}

		bool good = false;
		for(int i = 1; i <= N; i++) {
			ll cur = gcd(gcd(pref[i-1], suff[i+1]), A[i]-1);
			if(A[i] >= 2 && cur >= 2) {
				good = true;
				A[i] -= 1;
				for(int j = 1; j <= N; j++) A[i] /= cur;
				break;
			}
		}

