vi solve(vi& a,int hoge)
{
	int n = len(a);
	int id = -1;
	int cnt = 0;
	rep(i,n){
		if(a[i] % 2 == 0){
			cnt++;
		}else{
			id = i;
		}
	}
	if(cnt % 2 == 0){
		if(n - cnt > 1){
			Show(hoge^0);
			return {-1};
		}else{
			if(a[id] == 1){
				Show(hoge^0);
				return {-1};
			}
			a[id]--;
			ll g = 0;
			rep(i,n){
				g = gcd(g,a[i]);
			}
			rep(i,n){
				a[i] /= g;
