bool f(vector<ll> a) {
  bool lose = true;
  for (ll i: a) if (i != 1) lose = false;
  if (lose) return true;
  int c = 0;
  for (ll i: a) if (i % 2 == 0) ++c;
  if (c % 2) return true;
  if (c < int(a.size()) - 1) return false;
  for (ll &i: a) {
    if (i == 1) return false;
    if (i % 2) --i;
  }
  ll g = a[0];
  for (ll i: a) g = __gcd(g, i);
  for (ll &i: a) i /= g;
  return !f(a);
}

