bool solve(vi &a){
	int e = 0, ei, one = 0;
	ll sum = 0;
	rep(i, n){
		if(a[i] % 2 == 0){
			e++;
			ei = i;
		}
		if(a[i] == 1) one++;
		sum += a[i] - 1;
	}

	if(one) return sum % 2;
	if(e % 2) return 1;

	if(e == n - 1){
