bool win(vector<int> v) {
	int S = accumulate(v.begin(), v.end(), 0) + len(v);
	if (S & 1) return true;
	vector<int> odd;
	f(i, len(v)) {
		if (v[i] & 1) odd.pb(i); 
	}
	if (len(odd) > 1 || v[odd[0]] == 1) return false;
	v[odd[0]]--;
	int g = 0;
	for (int i = 0; i < len(v); i++)
		g = __gcd((unsigned int)v[i], (unsigned int)g);
	for (int i = 0; i < len(v); i++)
		v[i] /= g;
	return !win(v);
}

