    cin >> a[i];
  }
  function<bool()> solve = [&]() {
    int even_n = 0, odd_n = 0, odd_e = -1;
    for (int i = 0; i < n; i++) {
      if (a[i] & 1) {
        odd_n++;
        odd_e = a[i];
      } else {
        even_n++;
      }
    }
    if (odd_n > 1) {
      if (even_n & 1) {
        return true;
      } else {
        return false;
      }
    } else if (odd_n == 1) {
      if (even_n & 1) {
        return true;
      } else {
        if (odd_e > 1) {
          for (int i = 0; i < n; i++) {
            a[i] /= 2;
          }
          return !solve();
        } else {
          return false;
        }
      } 
    } else {
      if (even_n & 1) {
        int pw_two_n = 0;
        int pw_two_e = -1;
        for (int i = 0; i < n; i++) {
          if (a[i] == 2) {
            return true;
          }
          int t = 0;
          int e = a[i];
          while (e % 2 == 0) {
            t++;
            e /= 2;
          }
          if (t > pw_two_n) {
            pw_two_n = t;
            pw_two_e = i;
          }
        }
        a[pw_two_e]--;
        return !solve();
        // return false;    
      } else {
        return false;
      }
    }
  };
