bool judge(vector<ll> &A) {
	ll N = A.size();

	bool fin = 1;
	rep(i, N)if (A[i] != 1) { fin = 0; }
	if (fin) { return 0; }

	ll e = 0, o = 0;

	ll oid;
	rep(i, N) {
		if (A[i] % 2 == 1) { o++; oid = i; }
		else { e++; }
	}

	if (e % 2 == 1) { return 1; }
	else if (o > 1) { return 0; }
	A[oid]--;

	ll g = A[0];
	rep(i, N) g = gcd(g, A[i]);
	rep(i, N) A[i] /= g;

	bool b = judge(A);
	return !b;

}

int main() {
	ll N;
	cin >> N;
	vector<ll> A(N);
	rep(i, N) cin >> A[i];


	if (judge(A)) { cout << "First" << endl; }
	else { cout << "Second" << endl; }

	system("PAUSE");
}

