bool solve(vi os, vi es) {
	if (es.size() % 2 == 1) {
		return 1;
	}

	if (os.size() >= 2) {
		return 0;
	}

	bool one = (es.size() == 0);
	for (int x : os) {
		if (x != -1) one = 0;
	}
	if (one) return 0;

	es.pb(os[0] - 1);
	int d = -1;
	for (int x : es) {
		if (d == -1) {
			d = x;
		} else {
			d = __gcd(d, x);
		}
	}

	vi _os, _es;
	for (int x : es) {
		int nx = x / d;
		if (nx & 1) {
			_os.pb(nx);
		} else {
			_es.pb(nx);
		}
	}
	return !solve(_os, _es);
}

