    cin >> a;

  int64_t rem = 0;
  int64_t odds = 0;

  int64_t g = as[0];

  for (auto &a : as) {
    rem += a - 1;
    if (a > 1 && a % 2 == 1)
      odds++;

    g = gcd(g, a);
  }

  bool win = rem % 2 == 1;
  if (g == 1) {
    cout << (win ? "First" : "Second") << endl;
  }

  if (odds == 1) {
