#include <cstdio>

int N, A[100010];

int gcd(int x, int y){
  if(x == 0 || y == 0) return x + y;
  if(x == 1 || y == 1) return 1;

  for(;;){
    int t = x % y;
    if(t == 0) return y;
    x = y; y = t;
  }

  return -1;
}

bool sim(){ // can first win?
  int s = 0;
  for(int i = 0; i < N; i++){
    s = (s + A[i]) % 2;
  }

  if(N % 2 == 1 && s == 0) return true;
  if(N % 2 == 0 && s == 1) return true;
  if(N % 2 == 0 && s == 0) return false;

  int oc = 0, oi = -1;
  for(int i = 0; i < N; i++){
    if(A[i] % 2 == 1){ oc++; oi = i; }
  }

  if(oc != 1 || A[oi] == 1) return false;

  A[oi]--;

  int g = 0;
  for(int i = 0; i < N; i++) g = gcd(g, A[i]);
  for(int i = 0; i < N; i++) A[i] /= g;

  return !sim();
}

int main(){
  scanf("%d", &N);
  for(int i = 0; i < N; i++) scanf("%d", &A[i]);

  puts(sim() ? "First" : "Second");

  return 0;
}

