		if (a[i] % 2 and a[i]!=1) pos = i;
		if (a[i] > 1) ok = true;
	}
	if (!ok) return false;
	if (n == 2) {
		if (pos == -1) {
			int k = max(a[0], a[1]);
			if (k%2) return false;
			return true;
		}
		if (br == 2) {
			a[pos]--;
			return !check();
		}
		if (a[0]>a[1]) a[0]--;
		else a[1]--;
		return !check();
	} else {
		int par = n - br; 
		if (br > 1 or pos == -1) {
			if (par%2) return true;
			return false;
		}
		a[pos]--;
