int main() {
  int n;
  cin >> n;
  vector<int> a(n);
  for (int i = 0; i < n; i++) {
    cin >> a[i];
  }
  function<bool()> solve = [&]() {
    int even_n = 0, odd_n = 0, odd_e = -1;
    for (int i = 0; i < n; i++) {
      if (a[i] & 1) {
        odd_n++;
        odd_e = a[i];
      } else {
        even_n++;
      }
    }
    cerr << "odd_n = " << odd_n << " even_n = " << even_n << endl;
    if (odd_n > 1) {
      if (even_n & 1) {
        return true;
      } else {
        return false;
      }
    } else if (odd_n == 1) {
      if (even_n & 1) {
        return true;
      } else {
        if (odd_e > 1) {
          int odd_id = -1;
          for (int i = 0; i < n; i++) {
            if (a[i] & 1) {
              assert(odd_id == -1);
              odd_id = i;
            }
          }
          a[odd_id]--;
          int g = 0;
          for (int i = 0; i < n; i++) {
            g = gcd(g, a[i]);
          }
          for (int i = 0; i < n; i++) {
            a[i] /= g;
          }
          bool ret = !solve();
          for (int i = 0; i < n; i++) {
            a[i] *= g;
            if (odd_id == i) {
              a[i]++;
            }
          }
          return ret;
        } else {
          return false;
        }
      } 
    } else {
      if (even_n & 1) {
        for (int i = 0; i < n; i++) {
          if (a[i] == 2) {
            return true;
          }
        }
        for (int i = 0; i < n; i++) {
          a[i]--;
          int g = 0;
          for (int j = 0; j < n; j++) {
            g = gcd(g, a[j]);
          }
          for (int j = 0; j < n; j++) {
            a[j] /= g;
          }  
          if (!solve()) {
            return true;
          }
          for (int j = 0; j < n; j++) {
            a[j] *= g;
          }
          a[i]++;
        }
        return false;
      } else {
        return false;
      }
    }
  };
  bool ans = solve();
  cout << (ans ? "First" : "Second") << '\n';
  return 0;
}

