int G(int a,int b)
{
	while (a&&b)a>b?a%=b:b%=a;
	return a+b;
}

int gcd(vector<int> v)
{
	int res=0;
	for (int i=0;i<v.size();i++)
	{
		res=G(res,v[i]);
	}
	return res;
}

vector<int> normalize(vector<int> v){
	int q=gcd(v);
	for (int i=0;i<v.size();i++)
	{
		v[i]/=q;
	}
	return v;
}

int solve(vector<int> v)
{
	sort(v.begin(),v.end());

	if (memo.find(v)!=memo.end())
		return memo[v];

	int res=-1;
	for (int i=0;i<v.size();i++)
	{
		if (v[i]==1)
			continue;
		vector<int> v2=v;
		v2[i]--;
		v2=normalize(v2);
		int here=solve(v2);
		if (here==-1)
		{
			res=1;
			break;
		}
	}
	memo[v]=res;
	return res;
}

long long count_moves(vector<int> v)
{
	long long res=0;
	for (int i=0;i<v.size();i++)
	{
		res+=v[i]-1;
	}
	return res;
}

int guess_solver(vector<int> v)
{
	long long here=count_moves(v);
	if (here%2==1)
		return 1;

	for (int i=0;i<v.size();i++)
	{
		if (v[i]==1)
			continue;
		vector<int> v2=v;
		v2[i]--;
		if (gcd(v2)==1)
			continue;
		v2=normalize(v2);
		if (guess_solver(v2)==-1)
			return 1;
	}
	return -1;
}

