bool PlayGame() {
	int even=0,odd=0;
	for (int i=1;i<=n;i++)
		a[i]&1?odd++:even++;
	if (even&1) return true;
	if (odd!=1) return false;
	int t=1;while (!(a[t]&1)) ++t;
