bool solve(int N, vll& A) {
    ll S = 0;
    for (ll a : A) S += a;

    if (N % 2 == 0) {
        if (S % 2 == 0) return false;
        else return true;
    } else {
        int num_odd = 0;
        for (ll a : A) num_odd += (a % 2);

        if (S % 2 == 0) return true;
        else if (num_odd > 1) return false;
        else {
            vll B(N);
            for (int i=0;i<N;i++) B[i] = A[i]/2;
            return !solve(N, B);
        }

    }
}

int main(){
    int N;
    cin >> N;

    vll A(N);

    for (int i=0;i<N;i++) cin >> A[i];

    if (N == 1) cout << "Second" << endl;

    if (solve(N, A)) {
        cout << "First" << endl;
    } else {
        cout << "Second" << endl;
    }

    return 0;

}

