bool solve(vector<int>& a)
{
	int even=0,odd=0;
	for(auto x:a) (x&1?odd:even)++;
	assert(odd>=1);
	if(even&1)
		return true;
	if(odd>=2)
		return false;
	for(auto& x:a) if(x&1){
