bool solve(int n, VI &a) {
    if (n == 1) {
        return false;
    }

    int sum = 0;
    FOR(i,0,n-1) {
        sum = (sum + a[i]) % 2;
    }

    if (1 - sum == n % 2) {
        return true;
    }

    if (n % 2) {
        bool found = false;
        FOR(i,0,n-1) if (a[i] % 2) {
            found = true;
            a[i]--;

            break;
        }

        if (!found) {
            return false;
        }

        int gcd = 0;
        FOR(i,0,n-1) {
            gcd = __gcd(gcd, a[i]);
        }

        FOR(i,0,n-1) {
            a[i] /= gcd;
        }

        FOR(i,0,n-1) if (a[i] % 2 == 0) {
            return false;
        }

        return true;
    }

    return false;
}

