int gcd(int x, int y) {
    while (x) {
        y %= x;
        swap(x, y);
    }
    return y;
}

bool End() {
    for (int i = 0; i < n; ++i)
        if (a[i] != 1)
            return false;
    return true;
}

bool win() {
    if (First(a))
        return true;

    if (End())
        return false;

    int cnt = 0;
    int at = -1;
    for (int i = 0; i < n; ++i)
        if (a[i] % 2 == 1) {
            ++cnt;
            if (a[i] > 1)
                at = i;
        }

    if (cnt != 1 || at == -1)
        return false;
    a[at]--;

    int g = 0;
    for (int i = 0; i < n; ++i)
        g = gcd(a[i], g);

    if (g == 1)
        return false;

    for (int i = 0; i < n; ++i)
        a[i] /= g;

    return !win();
}

