const int MAXN=100005;
int n,a[MAXN];
int gcd(int a,int b){return a==0?b:gcd(b%a,a);}
void A(){puts("First");exit(0);}
void B(){puts("Second");exit(0);}
void final(int o)
{
	int cnt=0;
	for(int i=1;i<=n;i++)cnt^=((a[i]-1)&1);
	if(cnt^o)A();else B();
}
void work(int o)
{
	for(int i=1;i<=n;i++)if(a[i]&1)--a[i];
	int d=0;for(int i=1;i<=n;i++)d=gcd(d,a[i]);
	for(int i=1;i<=n;i++)a[i]/=d;
	for(int i=1;i<=n;i++)if(a[i]==1)final(o^1);
	int cnt=0;
	for(int i=1;i<=n;i++)cnt+=!(a[i]&1);
	if(cnt&1)o?B():A();
	if(n-1>cnt)o?A():B();
	work(o^1);
}
int main()
{
	n=read();int cnt=0;
	for(int i=1;i<=n;i++)a[i]=read();
	for(int i=1;i<=n;i++)if(a[i]==1)final(0);
	for(int i=1;i<=n;i++)cnt+=!(a[i]&1);
	if(cnt&1)A();
	if(n-1>cnt)B();
	work(0);
	return 0;

}

