typedef enum{
	win,
	lose
}res;

res judge(int *A, int N){
	int i, evennum = 0, one = 0;
	for(i = 0; i < N; i++){
		if(A[i] % 2 == 0){
			evennum++;
		}
		else if(A[i] == 1){
			one++;
		}
		A[i] /= 2;
	}
	if(evennum == N){
		return judge(A, N);
	}
	else if(evennum % 2 == 1){
		return lose;
	}
	else if(evennum < N - 1 || one > 0){
		return win;
	}
	else{
		return !judge(A, N);
	}
}

