int N;

int countOdd(const vector<int> &A)
{
    int n = 0;
    for (auto v : A)
    {
        if (v == 1) return 100;
        n += (v & 1);
    }
    return n;
}

lint calcRes(const vector<int> &A)
{
    lint res = -A.size();
    for (auto v : A) res += v;
    return res;
}

