    static class TaskD {
        public void solve(int testNumber, InputReader in, OutputWriter out) {
            int n = in.readInt();
            int[] a = IOUtils.readIntArray(in, n);
            String first = "First";
            String second = "Second";
            while (true) {
                if ((ArrayUtils.sumArray(a) - n) % 2 == 1) {
                    out.printLine(first);
                    return;
                }
                int qty = 0;
                for (int i : a) {
                    qty += i % 2;
                }
                if (qty != 1) {
                    out.printLine(second);
                    return;
                }
                int gcd = 0;
                for (int i = 0; i < n; i++) {
                    if (a[i] % 2 == 1) {
                        if (a[i] == 1) {
                            out.printLine(second);
                            return;
                        }
                        a[i]--;
                    }
                    gcd = IntegerUtils.gcd(a[i], gcd);
                }
                for (int i = 0; i < n; i++) {
                    a[i] /= gcd;
                }
                String temp = first;
                first = second;
                second = temp;
            }
        }

    }

