    static class TaskD {
        public void solve(int testNumber, InputReader in, OutputWriter out) {
            int n = in.readInt();
            int[] a = IOUtils.readIntArray(in, n);
            if ((ArrayUtils.sumArray(a) - n) % 2 == 1) {
                out.printLine("First");
                return;
            }
            int qty = 0;
            for (int i : a) {
                qty += i % 2;
            }
            if (qty != 1) {
                out.printLine("Second");
                return;
            }
            int gcd = 0;
            for (int i = 0; i < n; i++) {
                if (a[i] % 2 == 1) {
                    a[i]--;
                }
                gcd = IntegerUtils.gcd(a[i], gcd);
            }
            if ((ArrayUtils.sumArray(a) / gcd - n) % 2 == 0) {
                out.printLine("Second");
            } else {
                out.printLine("First");
            }
        }

