			int o = 0;
			for(int v : a){
				h ^= v-1;
				o += v&1;
			}
			h &= 1;
			assert o >= 1;
			if(o == 2){
				if(h == 1){
					for(int i = 0;i < n;i++){
						a[i] = a[i]/2*2;
					}
					return go(fall(a));
				}else{
					return false;
				}
			}else{
				if(h == 0){
					for(int i = 0;i < n;i++){
						a[i] = a[i]/2*2;
					}
					return !go(fall(a));
				}else{
					return true;
				}
			}
		}else{
			if(Arrays.stream(a).anyMatch(x -> x == 1)){
				int h = 0;
				for(int v : a){
					h ^= v-1;
				}
				h &= 1;
				return h == 1;
			}
			int h = 0;
			int o = 0;
			for(int v : a){
				h ^= v-1;
				o += v&1;
			}
			h &= 1;
			assert o >= 1;
			if(o >= 2){
				return h == 1;
			}else{
				if(h == 0){
					for(int i = 0;i < n;i++){
						a[i] = a[i]/2*2;
					}
					return !go(fall(a));
				}else{
					return true;
				}
			}
		}
	}

	static int[] fall(int[] a)
	{
		int g = 0;
		for(int i = 0;i < a.length;i++){
			g = gcd(a[i], g);
		}
		for(int i = 0;i < a.length;i++){
			a[i] /= g;
		}
		return a;
	}

	public static int gcd(int a, int b) {
		while (b > 0) {
			int c = a;
			a = b;
			b = c % b;
		}
		return a;
	}


