	boolean solve(int[] a) {
		int n = a.length;
		int odd = 0;
		boolean oneExists = false;
		long sum = 0;
		for (int i = 0; i < n; i++) {
			if (a[i] % 2 != 0) odd++;
			sum += a[i] - 1;
			if (a[i] == 1) oneExists = true;
		}
		if (sum % 2 != 0) 
			return true;
		else if (oneExists || odd != 1)
			return false;
		else {
			for (int i = 0; i < n; i++) {
				if (a[i] % 2 == 0) a[i] /= 2;
				else a[i] = (a[i] - 1) / 2;
			}
			return !solve(a);
		}
	}

