	private static void solve() {
		int n = ni();
		int[] a = na(n);

		boolean flg = false;
		while (true) {
			int odd = 0;
			int even = 0;
			int oddIdx = 0;
			for (int i = 0; i < n; i ++) {
				if (a[i] % 2 == 0) {
					even ++ ;
				} else {
					odd ++;
					oddIdx = i;
				}
			}

			if (even % 2 == 1) {
				System.out.println(flg ? "Second" : "First");
				return;
			} else if (odd != 1 || a[oddIdx] == 1) {
				System.out.println(flg ? "First" : "Second");
				return;
			} else {
				// odd == 1 , even % 2 == 0
				a[oddIdx] --;
				int g = a[0];
				for (int i = 0; i < n; i ++) {
					g = gcd(a[i], g);
				}
				for (int i = 0; i < n; i ++) {
					a[i] /= g;
				}
			}
			flg = !flg;
		}
	}

