public class Main {


  private static void solve() {
    int n = ni();
    int[] a = na(n);

    while (true) {
      int even = 0;
      int odd = 0;
      int oddNum = 0;
      for (int v : a) {
        if (v % 2 == 0) {
          even ++;
        } else {
          odd ++;
          oddNum = v;
        }
      }

      if (even % 2 == 1) {
        System.out.println("First");
        break;
      } else if (even % 2 == 0 && (odd != 1 || oddNum == 1)) {
        System.out.println("Second");
        break;
      }

      int gcd = 0;
      for (int i = 0; i < n; i ++) {
        if (a[i] % 2 == 1) {
          a[i] --;
        }
        gcd = gcd == 0 ? a[i] : gcd(a[i], gcd);
      }
      for (int i = 0; i < n; i ++) {
        a[i] /= gcd;
      }
    }
  }


  public static int gcd(int a, int b) {
    while (b > 0){
        int c = a;
        a = b;
        b = c % b;
    }
    return a;
  }

