            foreach (var x in a)
                sum += x - 1;
            var seg = new RMQSegmentTree(n);
            for (int i = 0; i < n; i++)
                seg.Update(i, a[i]);
            var otaku = a.Sum();
            var all = 0;
            for (int i = 0; i < n; i++)
            {
                if (a[i] == 1) continue;
                var l = seg.Query(0, i);
                var r = seg.Query(i + 1, n);
                var x = a[i] - 1;
                x = MathEx.GCD(x, l);
                x = MathEx.GCD(x, r);
                if (x != 1)
