using System;
using System.Linq;//リストの使用
using System.Collections.Generic;
using System.Text;//テキストの高速出力に必要
class Program
{
	static void Main()
	{
		int n = int.Parse(Console.ReadLine());
    long[] nums = Array.ConvertAll(Console.ReadLine().Split(' '),long.Parse);

		Console.WriteLine(Answer(nums)?"First":"Second");
	}

  static bool Answer(long[] nums)
  {
    int n = nums.Length;
    int evenCount = 0;
    int oddNum = 0;
    long oddMemo = 0;
    for(int i = 0; i < n; i++)
    {
      if(nums[i] % 2 == 0) evenCount++;
      else
      {
        oddNum = i;
        oddMemo = nums[i];
      }
    }

    if(evenCount % 2 == 1) return true;
    else if(evenCount != n-1 || oddMemo == 1) return false;
    else
    {
      nums[oddNum] = oddMemo-1;
      long gcd = nums[0];
      for(int i = 1; i < n; i++)
      {
        gcd = Gcd(gcd,nums[i]);
      }

      for(int i = 0; i < n; i++)
      {
        nums[i] /= gcd;
      }
      return !Answer(nums);
    }
  }

  static long Gcd(long a, long b) 
      {//引数a,bの最大公約数を返す
           if (a < b)
               return Gcd(b, a);//入れ替え
           while (b != 0) 
           {
               long changeTo = a % b;
               a = b;
               b = changeTo;
           }
           return a;
       }

}

