    public class Solver
    {
        public void Solve()
        {
            var n = sc.Integer();
            var a = sc.Long(n);
            Action dfs = null;
            dfs = () =>
             {
                 var sum = a.Sum();
                 if (sum == n) return;
                 if ((sum - n) % 2 == 1)
                     throw new Exception();
                 var odd = a.Sum(x => x % 2);
                 if (odd > 1) return;
                 var gcd = 0L;
                 for (int i = 0; i < n; i++)
                     if (a[i] % 2 == 1) gcd = MathEx.GCD(gcd, a[i] - 1);
                     else gcd = MathEx.GCD(gcd, a[i]);
                 if (gcd == 1) return;
                 for (int i = 0; i < n; i++)
                     a[i] /= gcd;

                 dfs();
             };
            try
            {
                dfs();
                IO.Printer.Out.WriteLine("Second");
            }
            catch
            {
                IO.Printer.Out.WriteLine("First");
            }
        }
        public IO.StreamScanner sc = new IO.StreamScanner(Console.OpenStandardInput());
        static T[] Enumerate<T>(int n, Func<int, T> f) { var a = new T[n]; for (int i = 0; i < n; ++i) a[i] = f(i); return a; }
