static public partial class MathEx
{

    static public Number GCD(Number n, Number m)
    {
        bool f = true;
        for (; n != 0 && m != 0; f ^= true)
        {
            if (f) m %= n;
            else n %= m;
        }
        return n == 0 ? m : n;
    }
    static public Number LCM(Number n, Number m)
    {
        return (n / GCD(n, m)) * m;
    }
    static public Number GCD(params Number[] a)
    {
        var gcd = a[0];
        foreach (var x in a)
            gcd = GCD(gcd, x);
        return gcd;
    }

}
