from fractions import gcd
def solve(a):
    n = len(a)
    s = [(x + 1) & 1 for x in a]
    ss = sum(s)
    if ss == n:
        a[0] -= 1
    elif ss == n - 1:
        for i in xrange(n):
            if s[i] == 0 and a[i] > 1:
                a[i] -= 1
                break
        else:
            return ss & 1
    else:
        return ss & 1
    g = reduce(gcd, a)
    return not solve([x / g for x in a])

