#!/usr/bin/env python3
# -*- coding: utf-8 -*-

from fractions import gcd

def readln(ch):
    _res = list(map(int,str(input()).split(ch)))
    return _res

def count(a):
    odd, even = 0,0
    for x in a:
        if x % 2 == 1: odd = odd + 1
        else: even = even + 1
    return odd,even

def deal(n,a):
    if n == 1:
        if a[0] % 2 == 0:
            return 'First'
        else:
            return 'Second'
    odd,even = count(a)
    if even == 0:
        return 'Second'
    if even % 2 == 1:
        return 'First'
    if odd > 1:
        return 'Second'
    if a[0] % 2 == 0:
        res = a[0]
    else:
        res = a[1]
    for i in range(0,n):
        if a[i] % 2 == 1:
            if a[i] == 1: return 'Second'
            a[i] = a[i] - 1
        res = gcd(a[i],res)
    return deal(n,list(map(lambda x: x//res,a)))

