fn main() {
    use std::cmp::{min, max};
    use std::io::Write;
    let out = std::io::stdout();
    let mut out = std::io::BufWriter::new(out.lock());
    macro_rules! print { ($($arg:tt)*) => (write!(out, $($arg)*).unwrap()); }
    macro_rules! println { () => (writeln!(out).unwrap()); ($($arg:tt)*) => (writeln!(out, $($arg)*).unwrap()); }

    let s: Vec<_> = readl!(String)
        .into_bytes()
        .into_iter()
        .map(|x| mint((x as u8 - 'a' as u8) as i16))
        .collect();
    // debug!(s);
    let n = readl!(usize);
    let m = s.len()/2;
    let mut g = AdjList::new(m+1);
    for _ in 0..n {
        let (mut l, mut r) = readl!(isize, isize);
        l -= 1;
        l = min(l, s.len() as isize-l);
        r = min(r, s.len() as isize-r);
        g.add_uedge(l as usize, r as usize, 1);
    }
    let g = g;

    let ss: Vec<_> = s.iter().take(s.len()/2)
        .zip(s.iter().rev().take(s.len()/2))
        .map(|(&x, &y)| x-y).collect();

    let sd: Vec<_> = ss.iter().cloned()
        .zip(vec![mint(0)].into_iter().chain(ss.iter().cloned())).map(|(x,y)| x-y)
        .chain(vec![mint(0)].into_iter()).collect();

    let mut used = vec![false; m+1];
    fn count(sd: &Vec<ModInt>, g: &AdjList<i32>, used: &mut Vec<bool>, p: usize) -> ModInt {
        if used[p] {
            mint(0)
        } else {
            used[p] = true;
            g.get(p).map(|e| {
                count(sd, g, used, e.to)
            }).fold(sd[p], |sum, x| sum+x)
        }
    }
    let mut ans = true;
    count(&sd, &g, &mut used, m);

    ans = (0..m).map(
        |x| {
            let res = count(&sd, &g, &mut used, x);
            res.val == 0
        }
    ).fold(ans, |ans, x| ans && x);
    if ans {
        println!("YES");
    } else {
        println!("NO");
    }
}

use std::io::{self, Stdin};
use std::str::{self, FromStr};
use std::error::Error;
use std::thread;

fn exec() {
    let mut sc = Scanner::new();
    let n: usize = sc.ne();
    let a: Vec<i64> = (0..n).map(|_| sc.ne()).collect();
    fn solve(mut a: Vec<i64>) -> bool {
        let sum = a.iter().fold(0, |acc, &x| acc + x - 1);
        if sum & 1 == 1 {
            return true;
        }
        let cnt_odd = a.iter().fold(0, |cnt, &x| (x & 1) + cnt);
        assert!(cnt_odd > 0);
        if cnt_odd > 1 {
            return false;
        }
        a = a.iter().map(|&x| if x & 1 == 1 { x - 1 } else { x }).collect();
        let gcd = a.iter().fold(0, |l, &r| __gcd(l, r));
        a = a.iter().map(|&x| x / gcd).collect();
        !solve(a)
    }
    println!("{}", if n > 1 && solve(a) { "First" } else { "Second" });
}

fn __gcd(a: i64, b: i64) -> i64 {
    match b {
        0 => a,
        _ => __gcd(b, a % b),
    }
}

const DEFAULT_STACK: usize = 16 * 1024 * 1024;
fn main() {
    let builder = thread::Builder::new();
    let th = builder.stack_size(DEFAULT_STACK);
    let handle = th.spawn(|| { exec(); }).unwrap();
    let _ = handle.join();
}

#[allow(dead_code)]
struct Scanner {
    stdin: Stdin,
    id: usize,
    buf: Vec<u8>,
}

#[allow(dead_code)]
impl Scanner {
    fn new() -> Scanner {
        Scanner {
            stdin: io::stdin(),
            id: 0,
            buf: Vec::new(),
        }
    }
    fn next_line(&mut self) -> Option<String> {
        let mut res = String::new();
        match self.stdin.read_line(&mut res) {
            Ok(0) => return None,
            Ok(_) => Some(res),
            Err(why) => panic!("error in read_line: {}", why.description()),
        }
    }
    fn next<T: FromStr>(&mut self) -> Option<T> {
        while self.buf.len() == 0 {
            self.buf = match self.next_line() {
                Some(r) => {
                    self.id = 0;
                    r.trim().as_bytes().to_owned()
                }
                None => return None,
            };
        }
        let l = self.id;
        assert!(self.buf[l] != b' ');
        let n = self.buf.len();
        let mut r = l;
        while r < n && self.buf[r] != b' ' {
            r += 1;
        }
        let res = match str::from_utf8(&self.buf[l..r]).ok().unwrap().parse::<T>() {
            Ok(s) => Some(s),
            Err(_) => panic!("parse error"),
        };
        while r < n && self.buf[r] == b' ' {
            r += 1;
        }
        if r == n {
            self.buf.clear();
        } else {
            self.id = r;
        }
        res
    }
    fn ne<T: FromStr>(&mut self) -> T {
        self.next::<T>().unwrap()
    }
}

macro_rules! input {
    (source = $s:expr, $($r:tt)*) => {
        let mut iter = $s.split_whitespace();
        let mut next = || { iter.next().unwrap() };
        input_inner!{next, $($r)*}
    };
    ($($r:tt)*) => {
        let stdin = std::io::stdin();
        let mut bytes = std::io::Read::bytes(std::io::BufReader::new(stdin.lock()));
        let mut next = move || -> String {
            bytes
                .by_ref()
                .map(|r| r.unwrap() as char)
                .skip_while(|c| c.is_whitespace())
                .take_while(|c| !c.is_whitespace())
                .collect()
        };
        input_inner!{next, $($r)*}
    };
}

macro_rules! input_inner {
    ($next:expr) => {};
    ($next:expr, ) => {};

    ($next:expr, $var:ident : $t:tt $($r:tt)*) => {
        let $var = read_value!($next, $t);
        input_inner!{$next $($r)*}
    };

    ($next:expr, mut $var:ident : $t:tt $($r:tt)*) => {
        let mut $var = read_value!($next, $t);
        input_inner!{$next $($r)*}
    };
}

macro_rules! read_value {
    ($next:expr, ( $($t:tt),* )) => {
        ( $(read_value!($next, $t)),* )
    };

    ($next:expr, [ $t:tt ; $len:expr ]) => {
        (0..$len).map(|_| read_value!($next, $t)).collect::<Vec<_>>()
    };

    ($next:expr, [ $t:tt ]) => {
        {
            let len = read_value!($next, usize);
            (0..len).map(|_| read_value!($next, $t)).collect::<Vec<_>>()
        }
    };

    ($next:expr, chars) => {
        read_value!($next, String).chars().collect::<Vec<char>>()
    };

    ($next:expr, usize1) => {
        read_value!($next, usize) - 1
    };

    ($next:expr, $t:ty) => {
        $next().parse::<$t>().expect("Parse error")
    };
}

#[allow(unused_imports)]
use std::cmp::{min, max};

fn main() {
    input!{
        n: usize,
        mut v: [usize; n]
    }
    let mut f = true;
    loop {
        let es = v.iter().filter(|&x| x % 2 == 0).count();
        if es % 2 == 1 {
            println!("{}", if f {"First"} else {"Second"});
            return;
        } else if n - es > 1 {
            println!("{}", if f {"Second"} else {"First"});
            return;
        }
        let mut g = 0;
        for i in 0..n {
            if v[i] == 1 {
                println!("{}", if f {"Second"} else {"First"});
                return;
            }
            if v[i] % 2 == 1 {
                v[i] -= 1;
            }
            g = gcd(g, v[i]);
        }
        f ^= true;
    }
}

fn gcd(n: usize, m: usize) -> usize {
    let mut l = min(n, m);
    let mut r = max(n, m);
    while l > 0 {
        let t = l;
        l = r % l;
        r = t;
    }
    r
}

